mapper.select:
select bdjl.id,bdjl.rq,sj.xm cysjXm,dd.ddh,wz.mc wzMc,cltz.jcsj,cltz.ccsj,bdjl.mz,dfgbjl.dfgbmz,fhdw.mc fhdwMc,bdjl.pz,dfgbjl.dfgbpz,shdw.mc shdwMc,bdjl.jz,dfgbjl.dfgbjz,yss.mc yssMc,cl.cph cyclCph,bdjl.bz from bang_dan_ji_lu bdjl left join ding_dan dd on bdjl.ddId=dd.id left join dui_fang_guo_bang_ji_lu dfgbjl on dfgbjl.ddId=dd.id left join wu_zi wz on dd.wzId=wz.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id left join shou_huo_dan_wei shdw on dd.shdwId=shdw.id left join yun_shu_shang yss on dd.yssId=yss.id left join si_ji sj on dd.cysjId=sj.id left join che_liang cl on dd.cyclId=cl.id left join che_liang_tai_zhang cltz on cltz.ddId=dd.id where 1=1
and dd.ddh like CONCAT('%',#{ddh},'%')
order by bdjl.rq desc limit #{rowNum},#{rows}
