mapper.select:
select dd.*,wzlx.mc wzlxMc,wz.mc wzMc,yss.mc yssMc,fhdw.mc fhdwMc,shdw.mc shdwMc from ding_dan dd left join wu_zi_lei_xing wzlx on dd.wzlxId=wzlx.id left join wu_zi wz on dd.wzId=wz.id
 left join yun_shu_shang yss on dd.yssId=yss.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id
 left join shou_huo_dan_wei shdw on dd.shdwId=shdw.id where dd.id=#{id}

select dd.* from ding_dan dd,ding_dan_zhuang_tai ddzt where dd.ddztId=ddzt.id and ddzt.mc=#{ddztMc}

 and dd.yjbfh=#{yjbfh}


 and dd.ejbfh=#{ejbfh}


 and dd.yjzt=#{yjzt}


 and dd.ejzt=#{ejzt}
