mapper.select:
select hm.*,hmzt.mc hmztMc,dl.mc dlMc from hao_ma hm left join hao_ma_zhuang_tai hmzt on hm.hmztId=hmzt.id left join ding_dan dd on hm.ddId=dd.id left join shou_huo_dan_wei shdw on dd.shdwId=shdw.id left join dui_lie dl on shdw.dlId=dl.id where 1=1
and dl.mc like CONCAT('%',#{dlMc},'%')
and hm like CONCAT('%',#{hm},'%')
and pdh like CONCAT('%',#{pdh},'%')
and hmztId=#{hmztId}
limit #{rowNum},#{rows}

select hm.* from hao_ma hm,ding_dan dd,hao_ma_zhuang_tai hmzt,shou_huo_dan_wei shdw,dui_lie dl where hm.ddId=dd.id and dd.shdwId=shdw.id and shdw.dlId=dl.id and hm.hmztId=hmzt.id and hmzt.mc='${@com.znczCxtc.entity.DingDanZhuangTai@PAI_DUI_ZHONG_TEXT}'
#{ymdDlId}
ORDER BY pdh LIMIT 0,1
